import { useEffect } from 'react';
import { LanguageConstants } from '../../Constants/LanguageConstants';
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
const LanguageDropdown = function({dropDownOpen, setDropDownOpen, language, setLanguage}){
    // const { id } = useParams();
    const id = window.location.pathname;
    const navigate = useNavigate();
    useEffect(()=>{
       language && console.log(language,"lang");
       const idParts = id.split("-");
       console.log(id,idParts);
// Remove the last element (pop it off)
idParts.pop();

// Add "fr" to the end of the remaining string
const newId = idParts.join("-") + `-${language.code}`;

// Use navigate to update the URL
console.log(id,idParts,newId);
navigate(newId);
    },[language.code])
    return (
        <div style={{ width: "auto", display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "start", position: "fixed", paddingRight: "50px", right: "0", top: "10px" }}>
        <div style={{width: "auto", display: "flex", flexDirection: "row", justifyContent: "center", alignItems: "center", cursor:"pointer"}} onClick={() => { setDropDownOpen(!dropDownOpen) }} >
          <img src={`/flags/${language}.svg`} style={{ width: "30px", height: "30px", borderRadius: "30px", border: "1px solid red", marginRight: "5px" }}/>
          <span>{!dropDownOpen?language.language:"Select Language"}</span>
        </div>
        {dropDownOpen && (
        <div>
          {LanguageConstants.map((e) => (
            <div
              key={e}
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                width: "auto",
                marginTop: "10px",
                cursor: "pointer"
              }}
              onClick={() => {
                setLanguage(e);
                setDropDownOpen(!dropDownOpen);
                console.log(id);

    //    window.location.reload();
              }}
            >
              <img
                src={`/flags/${e}.svg`}
                // alt={`${e.language} flag`}
                style={{
                  width: "30px",
                  height: "30px",
                  borderRadius: "30px",
                  border: "1px solid red",
                  marginRight: "10px"
                }}
              />
              <span>{e.language}</span>
            </div>
          ))}
        </div>
      )}
      </div>
    )
}

export default LanguageDropdown;
