import React, { useEffect, useState } from 'react'
import { createClient } from "contentful"
import './App.scss';
import BlogList from './Blog/BlogList';
import SingleBlog from './Blog/SingleBlog';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import ShowData from './Component/ShowData';

function App() {
  const [data, setData] = useState([]);
  const client = createClient({ 
    space: "4fvznm2u5cg7", 
    accessToken: "yy1xv31CKjpp1pbpgxGitvx7a9JhNtb-AhH9Q1sQ-vk" 
  })

  const [locale, setLocale] = useState({
    language: "English",
    code: "en-US",
    flag: "/flag/en.svg"
  });
  // useEffect(() => {
  //   const getAllEntries = async () => {
  //     try {
  //       await client.getEntries().then((entries) => {
  //         setdata(entries)
  //       })
  //     } catch (error) {
  //       console.log(`Error fetching authors ${error}`);
  //     }
  //   };
  //   getAllEntries();
  // }, [])
  useEffect(() => {
    const getAllEntries = async () => {
      try {
        await client.getEntries({ locale }).then((entries) => {
          setData(entries);
        });
      } catch (error) {
        console.log(`Error fetching authors ${error}`);
      }
    };
    getAllEntries();
  }, [locale]);

  useEffect(()=>{
    console.log(data, "lander");
  },[data])

  return (
    <Router>
      <Routes>
        <Route path="/blogDetails/:id" element={<SingleBlog />} />
        <Route path="/blogList" element={<BlogList />} />
        <Route path="/" element={<BlogList />} />
        <Route path="/:id" element={<ShowData {...{data, locale, setLocale}} />} />
      </Routes>
    </Router>
  );
}

export default App;
