import { createClient } from "contentful"
import './App.scss';
import BlogList from './Blog/BlogList';
import SingleBlog from './Blog/SingleBlog';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import ShowData from './Component/ShowData';
import LanguageDropdown from './Component/LanguageDropDown/LanguageDropdown';

function App() {
  const [dropDownOpen, setDropDownOpen] = useState(false);
  const client = createClient({ 
    space: "4fvznm2u5cg7", 
    accessToken: "yy1xv31CKjpp1pbpgxGitvx7a9JhNtb-AhH9Q1sQ-vk" 
  })

  const [language, setLanguage] = useState({
    language: "English",
    code: "en"
  });

  // useEffect(() => {
  //   const getAllEntries = async () => {
  //     try {
  //       await client.getEntries({ locale: language.code }).then((entries) => {
  //         setData(entries);
  //       });
  //     } catch (error) {
  //       console.log(`Error fetching authors ${error}`);
  //     }
  //   };
  //   getAllEntries();
  // }, [language.code]);

  return (
    <Router>
      <LanguageDropdown {...{language, setLanguage,dropDownOpen, setDropDownOpen}}/>
      <Routes>
        <Route path="/blogDetails/:id" element={<SingleBlog />} />
        <Route path="/blogList" element={<BlogList />} />
        <Route path="/" element={<BlogList />} />
        <Route path="/:id" element={<ShowData {...{ language, setLanguage}} />} />
      </Routes>
    </Router>
  );
}

export default App;
